mapper.delete:
DELETE
from users_batis ub
where ub.id = #{id}

mapper.select:
select *
from users_batis ub
where ub.username = #{username}

mapper.update:
update users_batis
set name     = #{user.name},
    age      = #{user.age},
    username = #{user.username},
    password = #{user.password}
where id = #{user.id};
